import { Injectable } from "@angular/core";
import * as customTitlebar from "custom-electron-titlebar";
import { remote, shell } from "electron";
import * as mousetrap from "mousetrap";
import { ShowService } from "./show.service";
import { RecentShowsService } from "./recent-shows.service";
import { DefaultScenesService } from "./default-scenes.service";
import { pkginfo } from "../_helpers/packageInfo";

@Injectable({
    providedIn: "root",
})
export class TitlebarService {
    titlebar: customTitlebar.Titlebar;
    constructor(
        private showService: ShowService,
        private recentShowsService: RecentShowsService,
        private defaultScenesService: DefaultScenesService,
    ) {
        const menu = this.getMenu();
        this.titlebar = new customTitlebar.Titlebar({
            backgroundColor: customTitlebar.Color.fromHex("#444"),
            icon: "assets/icons/favicon.png",
            enableMnemonics: true,
            hideWhenClickingClose: true,
            itemBackgroundColor: customTitlebar.Color.fromHex("#094771"),
            menu,
        });
    }

    private getMenu() {
        const recentShows = this.recentShowsService.get();
        const menuTemplate: any[] = [
            {
                label: "File",
                submenu: [
                    {
                        label: "New file",
                        accelerator: "Ctrl+N",
                        click: () => this.showService.new(),
                    },
                    {
                        type: "separator",
                    },
                    {
                        label: "Open file",
                        accelerator: "Ctrl+O",
                        click: () => this.showService.open(),
                    },
                    {
                        label: "Open recent",
                        submenu: recentShows && recentShows.length ? recentShows.map((s) => ({
                            label: s,
                            click: () => {
                                this.showService.open(s);
                                this.titlebar.updateMenu(this.getMenu());
                            },
                        })) : [{
                            label: "No recent shows",
                            enabled: false,
                        }],
                    },
                    {
                        type: "separator",
                    },
                    {
                        label: "Save",
                        accelerator: "Ctrl+S",
                        click: () => this.showService.save(),
                    },
                    {
                        label: "Save as...",
                        accelerator: "Ctrl+Shift+S",
                        click: () => this.showService.saveAs(),
                    },
                    {
                        type: "separator",
                    },
                    {
                        label: "Quit",
                        accelerator: "Ctrl+Q",
                        click: () => remote.app.quit(),
                    },
                ],
            },
            {
                label: "Slide",
                submenu: [
                    {
                        label: "Import slides...",
                        accelerator: "Ctrl+I",
                        click: () => this.showService.messages.next("importSlides"),
                    },
                    {
                        type: "separator",
                    },
                    {
                        label: "Rename slide",
                        accelerator: "F2",
                        click: () => this.showService.messages.next("renameSlide"),
                    },
                    {
                        label: "Remove slide",
                        accelerator: "Del",
                        click: () => this.showService.messages.next("removeSlide"),
                    },
                    {
                        type: "separator",
                    },
                    {
                        label: "Slide Properties",
                        accelerator: "Strg+W",
                        click: () => this.showService.messages.next("slideProperties"),
                    },
                ],
            },
            {
                label: "View",
                submenu: [
                    {
                        label: "Next slide",
                        click: () => this.showService.messages.next("viewNextSlide"),
                    },
                    {
                        label: "Previous slide",
                        click: () => this.showService.messages.next("viewPreviousSlide"),
                    },
                    {
                        type: "separator",
                    },
                    {
                        label: "First slide",
                        click: () => this.showService.messages.next("viewFirstSlide"),
                    },
                    {
                        label: "Last slide",
                        click: () => this.showService.messages.next("viewLastSlide"),
                    },
                    {
                        type: "separator",
                    },
                    {
                        label: "Custom Logo",
                        accelerator: "F2",
                        click: () => this.defaultScenesService.transitionTo("customLogo"),
                    },
                    {
                        label: "Blackout",
                        accelerator: "F1",
                        click: () => this.defaultScenesService.transitionTo("black"),
                    },
                    {
                        type: "separator",
                    },
                    {
                        label: "AGView logo",
                        accelerator: "F4",
                        click: () => this.defaultScenesService.transitionTo("logo"),
                    },
                ],
            },
            {
                label: "Window",
                submenu: [
                    { role: "minimize" },
                    { role: "close" },
                ],
            },
            {
                role: "help",
                submenu: [
                    {
                        label: "Learn More",
                        click: async () => {
                            await shell.openExternal("https://github.com/hrueger/AGView");
                        },
                    },
                    {
                        label: "Found a bug?",
                        click: async () => {
                            await shell.openExternal("https://github.com/hrueger/AGView/issues/new");
                        },
                    },
                    {
                        type: "separator",
                    },
                    {
                        label: "About",
                        click: () => this.about(),
                    },
                ],
            },
        ];
        const menu = remote.Menu.buildFromTemplate(menuTemplate);

        // set shortcuts
        for (const m of menuTemplate) {
            for (const entry of m.submenu) {
                if (entry.accelerator) {
                    // remote.globalShortcut.register(entry.accelerator, () => entry.click());
                    mousetrap.bind(entry.accelerator.toLowerCase(), () => entry.click());
                }
            }
        }

        return menu;
    }

    public setTitle(t: string) {
        this.titlebar.updateTitle(t);
    }

    private about() {
        const aboutWindow = new remote.BrowserWindow({
            parent: remote.getCurrentWindow(),
            autoHideMenuBar: true,
            frame: false,
            webPreferences: {
                nodeIntegration: true,
                webSecurity: false,
            },
        });
        const options = {
            title: pkginfo.productName,
            description: pkginfo.description,
            bugs: pkginfo.bugs,
            homepage: pkginfo.homepage,
            version: pkginfo.version,
            copyright: `&copy ${new Date().getFullYear()}, ${pkginfo.author.name}`,
        };
        const imageUrl = "data:image/png;base64,";
        const html = `<head> <meta http-equiv="Content-Type" content="text/html; charset=utf-8"/> <meta name="viewport" content="width=device-width, initial-scale=1.0"/><title>About AGView</title><style type="text/css"> body, html{width: 100%; height: 100%; -webkit-user-select: none; user-select: none; -webkit-app-region: drag;}body{margin: 0; display: flex; flex-direction: column; justify-content: center; align-items: center; color: rgb(51,51,51); background-color: rgb(238,238,238); font-size: 12px; font-family: "Helvetica", "Arial", sans-serif;}.logo{width: 200px; -webkit-user-select: none; user-select: none;}.title, .copyright, .description{margin: 0.2em;}.clickable{cursor: pointer;}.description{margin-bottom: 1em; text-align: center;}.versions{border-collapse: collapse; margin-top: 1em;}.copyright, .versions{color: rgb(153,153,153);}.buttons{margin-bottom: 1em; text-align: center;}.buttons button{margin-top: 1em; width: 100px; height: 24px;}.link{cursor: pointer; color: rgb(128,160,194);}.bug-report-link{position: absolute; right: 0.5em; bottom: 0.5em;}.clickable, .bug-report-link, .buttons button{-webkit-app-region: no-drag;}</style></head><body> <script>const customTitlebar=require("custom-electron-titlebar"); new customTitlebar.Titlebar({menu: undefined, titleHorizontalAlignment: "left", minimizable: false, maximizable: false}); </script><div class="logo clickable"><img id="openHomepage" alt="App icon" height="200" src="${imageUrl}"></div><h2 class="title clickable">${options.title}</h2><h2>v${options.version}</h2><h3 class="description">${options.description}</h3><div class="copyright">${options.copyright}</div><table class="versions"> <tr> <td>electron</td><td> : 7.1.0</td></tr><tr> <td>chrome</td><td> : 78.0.3904.94</td></tr><tr> <td>node</td><td> : 12.8.1</td></tr><tr> <td>v8</td><td> : 7.8.279.19-electron.0</td></tr></table><div class="buttons"></div><footer class="footer"> <div class="link bug-report-link" id="openBugUrl">Report an issue</div></footer><script>document.querySelector("%23openBugUrl").addEventListener("click", function() {require("electron").shell.openExternal('${encodeURI(options.bugs)}')});document.querySelector("%23openHomepage").addEventListener("click", function() {require("electron").shell.openExternal('${encodeURI(options.homepage)}')});</script></body>`;
        aboutWindow.loadURL(`data:text/html;charset=utf-8,${html}`);
    }
}
